import React, { Component } from "react";
import "../App.css";
import { Bar } from "react-chartjs-2";
import axios from "axios";
import _ from "lodash";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      labels: ["twitter stream"],
      datasets: [
        {
          id: 'positif',
          label: "sentimen positif",
          backgroundColor: "rgba(75,192,192,1)",
          data: [5]
        },
        {
          id: 'netral',
          label: "sentimen netral",
          backgroundColor: "rgb(228,227,115)",
          data: [2]
        },
        {
          id: 'negatif',
          label: "sentimen negatif",
          backgroundColor: "rgb(227,21,55)",
          data: [3]
        }
      ],
      data: []
    };
  }

  setInterval = () => {
    setInterval(this.getData, 10000);
  };

  getData = () => {
    const urlData = "https://bluetweets.herokuapp.com/sentiment";
    let data = [];
    axios
      .get(
        urlData,
        {},
        {
          headers: {
            "content-type": "application/json"
          }
        }
      )
      .then(response => {
        if (response.status === 200 && response.data !== "undefined") {
          data.push(response.data);
          this.getDataResponse(data);
        }
      })
      .catch(function(err) {
        return console.log("error get data", err);
      });
  };

  componentDidMount() {
    this.getData();
  }

  getDataResponse = value => {
    let dataSet = this.state;
    let datanormal, datanegatif, datapositif = [];
    _.map(value, v => {
        let data = [...v]
      _.map(data, item => {
        console.log("data", item.value)
        switch (item.value) {
          case '1':
            let index = _.filter(data, {value: '1'})
            datapositif = index.length;
            break;
          case '-1':
            let index2 = _.filter(data, {value: '-1'})
            datanegatif = index2.length;
          break;
          default:
            let indexData = _.filter(data, {value: '0'})
            datanormal = indexData.length;
            break;
        }
      });
    });

    if(!!datanormal){
      console.log("cek data input", datanormal, datapositif, datanegatif)
      _.map(this.state.datasets, v =>{
        if(v.id == 'positif'){
          console.log("dataSets positif", v.data[0] = datapositif)
        }else if(v.id == 'negatif'){
          console.log("dataSets negatif", v.data[0] = datanegatif)
        }else{
          console.log("dataSets normal", v.data[0] = datanormal)
        }
      })
    }
    return dataSet;
  };

  render() {
    return (
      <div className="Container">
        <div className="App-header">
          <p>Tugas big data analytics</p>
        </div>
        <div className="Body">
          <Bar
            data={this.getDataResponse()}
            options={{
              title: {
                display: true,
                text: "Sentimen Analisis Presiden",
                fontSize: 20
              },
              legend: {
                display: true,
                position: "bottom"
              }
            }}
          />
        </div>
      </div>
    );
  }
}

export default Home;
