        {},
        {
          headers: {
            "content-type": "application/json"
          }
        }
      )
      .then(response  => {
        data.push(response)
        return data;
      })
      .catch(function(err) {
        return console.log("error get data", err);
      });

      this.setState({data})
      return data
  }

  componentDidMount() {
    this.setInterval();
  }

  getDataResponse = (value) =>{
      let dataSet = this.state
