      return dataSet
  }


  render() {
    return (
      <div className="Container">
        <div className="App-header">
          <p>Tugas big data analytics</p>
        </div>
        <div className="Body">
          <Bar
            data={this.getDataResponse()}
            options={{
              title: {
                display: true,
                text: "Sentimen Analisis BDA",
                fontSize: 20
              },
              legend: {
                display: true,
                position: "bottom"
              }
            }}
          />
        </div>
        {/* <Legend labels={ this.state.labels } colors={ this.state.colors } /> */}
      </div>
    );
  }
}

export default Home;
