import React, { Component } from 'react';
import '../App.css'
import {Bar} from 'react-chartjs-2';

const state = {
    labels: ['January', 'February', 'March',
             'April', 'May'],
    datasets: [
      {
        label: 'Rainfall',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [65, 59, 80, 81, 56]
      }
    ]
}


class Home extends Component {
    constructor(props){
        super(props)

        this.state={
            data: [30, 20, 10],
			series: ['France'],
			labels: ['netral', 'negatif', 'positif'],
			colors: ['#F2317A', '#FF9824', '#58CF6C']
        }
    }

    render() {
        return (
            <div className="Container">
                <div className="App-header">
                    <p>tugas big data analytics</p>
                </div>
                <div className="Body"> 
                <Bar
                    data={state}
                    options={{
                        title:{
                        display:true,
                        text:'Average Rainfall per month',
                        fontSize:20
                        },
                        legend:{
                        display:true,
                        position:'right'
                        }
                    }}
                />            
                </div>
                {/* <Legend labels={ this.state.labels } colors={ this.state.colors } /> */}
            </div>
        );
    }
}

export default Home
